import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import LanguageBreakdown from './LanguageBreakdown';

Modal.setAppElement('#root');

function TestResultsDashboard({ summary, allBreakdowns, loading }) {
  const {
    runId = '‚Äî',
    total = 0,
    passed = 0,
    failed = 0,
    duration = '‚Äî',
    servicesTested = '‚Äî',
  } = summary || {};

  const passRate = total > 0 ? ((passed / total) * 100).toFixed(2) : '0.00';

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [failInfo, setFailInfo] = useState([]);
  const [failInfoLoading, setFailInfoLoading] = useState(false);
  const [failInfoError, setFailInfoError] = useState('');

  const handleModalToggle = async () => {
    if (!isModalOpen && runId !== '‚Äî') {
      setFailInfoLoading(true);
      setFailInfoError('');
      try {
        const url = `https://dtpuya01xb.execute-api.us-east-1.amazonaws.com/prod/myresource?runId=${runId}`;
        const res = await fetch(url);
        if (!res.ok) throw new Error('Failed to fetch fail info');
        const json = await res.json();
        setFailInfo(json);
      } catch (err) {
        console.error('Failed to load fail info:', err);
        setFailInfoError('Error loading fail information.');
        setFailInfo([]);
      } finally {
        setFailInfoLoading(false);
      }
    }

    setIsModalOpen(!isModalOpen);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="w-12 h-12 border-4 border-blue-500 border-dashed rounded-full animate-spin"></div>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 p-6">
      {/* Test Summary */}
      <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-200">
        <h2 className="text-2xl font-semibold mb-4 text-gray-800 flex items-center">
          üìä Test Summary
        </h2>
        <div className="grid gap-4 text-gray-700 text-lg">
          <div className="flex justify-between"><span>üÜî <strong>Run Id: </strong></span><span>{runId}</span></div>
          <div className="flex justify-between"><span>üîß <strong>Total Services Tested: </strong></span><span>{servicesTested}</span></div>
          <div className="flex justify-between"><span>‚úÖ <strong>Total Tests Run: </strong></span><span>{total}</span></div>
          <div className="flex justify-between"><span>üü¢ <strong>Passed: </strong></span><span>{passed}</span></div>
          <div className="flex justify-between"><span>üî¥ <strong>Failed: </strong></span><span>{failed}</span></div>
          <div className="flex justify-between"><span>üìà <strong>Pass Rate: </strong></span><span>{passRate}%</span></div>
          <div className="flex justify-between"><span>‚è±Ô∏è <strong>Execution Time: </strong></span><span>{duration}</span></div>
          <button
            onClick={handleModalToggle}
            className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-lg"
          >
            View Fail Information
          </button>
        </div>
      </div>

      {/* Language Breakdown */}
      <LanguageBreakdown allBreakdowns={allBreakdowns} />

      {/* Modal */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={handleModalToggle}
        contentLabel="Fail Information"
        className="fixed top-1/4 left-1/2 transform -translate-x-1/2 bg-white rounded-xl p-6 w-11/12 max-w-2xl shadow-lg outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50"
      >
        <div>
          <h3 className="text-xl font-semibold mb-4 text-gray-800">Fail Information</h3>

          {failInfoLoading && <p className="text-blue-600">Loading...</p>}
          {failInfoError && <p className="text-red-600">{failInfoError}</p>}

          <div className="space-y-4">
            {failInfo.map((item, index) => (
              <div key={index} className="border p-4 rounded-lg bg-gray-50">
                <h4 className="font-semibold text-lg text-gray-800">{item.serviceName}</h4>
                <p className="text-sm text-red-600">{item.errorSummary}</p>
              </div>
            ))}
          </div>

          <button
            onClick={handleModalToggle}
            className="mt-4 px-4 py-2 bg-red-500 text-white rounded-lg"
          >
            Close
          </button>
        </div>
      </Modal>
    </div>
  );
}

export default TestResultsDashboard;
